ct(0, 0, canvas.width, canvas.height); // Set background color ctx.fillStyle = backgroundColor; ctx.fillRect(0, 0, canvas.width, canvas.height); // Set fill color for the line ctx.fillStyle = color; // Adjust the rectangle width based on current percentage ctx.fillRect(0, 0, canvas.width * (currentPercentage / 100), canvas.height); // Request another animation frame for smooth animation requestAnimationFrame(animateFill); }; animateFill(); // Start the animation return canvas; } function initMap() { const mapStyles = [ { featureType: \\"poi\\", stylers: [ { visibility: \\"off\\", }, ], }, { featureType: \\"poi.park\\", stylers: [ { visibility: \\"off\\", }, ], }, { featureType: \\"transit\\", stylers: [ { visibility: \\"off\\", }, ], }, ]; const mapOptions = { center: { lat: 21.382325, lng: -8.170154652 }, zoom: 3, styles: mapStyles, }; map = new google.maps.Map(document.getElementById(\\"map\\"), mapOptions); // Add a click event listener to the map clickListener = map.addListener(\\"click\\", (event) => { const clickLocation = event.latLng; // Get the latitude and longitude of the click // Create a new marker marker = new google.maps.Marker({ position: clickLocation, map: map, // Set the map where the marker will be displayed draggable: true, // Set draggable to true }); // (Optional) Add additional customization to the marker here, // such as setting an icon or info window // Remove the click event listener after adding the marker google.maps.event.removeListener(clickListener); // Add functionality after clicking the map createSubmitButton(map, clickLocation); }); } //nextbutton const nextButton = document.createElement(\\"button\\"); nextButton.id = \\"nextButton\\"; nextButton.textContent = \\"Next\\"; // Customize button text as needed nextButton.className = \\"nextbutton\\"; // Apply CSS animation class for easy management nextButton.classList.add(\\"nextButtonAnimation\\"); // Function to create and add the button function createSubmitButton(map, clickLocation) { const buttonsDiv = document.getElementById(\\"buttons\\"); if (!buttonsDiv) { console.error(\\"Element with ID 'buttons' not found!\\"); return; } const submitButton = document.createElement(\\"button\\"); submitButton.textContent = \\"Submit\\"; // Customize button text submitButton.classList.add(\\"button\\"); // Add class 'button' submitButton.addEventListener(\\"click\\", () => { // Handle button click event here (e.g., send clickLocation data) console.log( \\"Button clicked! Latitude:\\", clickLocation.lat(), \\"Longitude:\\", clickLocation.lng() ); // Get the current marker position when the button is pressed const markerPosition = marker.getPosition(); // Calculate distance between marker and StreetPoints const distanceInMeters = google.maps.geometry.spherical.computeDistanceBetween( new google.maps.LatLng(streetLatitude, streetLongitude), markerPosition ); const roundedDistanceInMeters = Math.floor(distanceInMeters); // Round down to the nearest meter console.log( \\"Distance to StreetPoints: \\" + roundedDistanceInMeters + \\" meters\\" ); // Adjust points based on distance let score = 5000 - roundedDistanceInMeters; if (score < 0) { score = 0; } totalScore += score; // Add current points to total possibleScore += 5000; roundScores.push(score); const message = \\"You scored \\" + score + \\" points\\"; // Update the 'results' div using DOM manipulation const resultsDiv = document.getElementById(\\"results\\"); resultsDiv.textContent = message; // Create a polyline between marker and StreetPoints const lineCoordinates = [ { lat: streetLatitude, lng: streetLongitude }, { lat: markerPosition.lat(), lng: markerPosition.lng() }, ]; const polyline = new google.maps.Polyline({ path: lineCoordinates, geodesic: true, strokeColor: \\"#FF0000\\", strokeOpacity: 1.0, strokeWeight: 2, }); // Set the polyline on the map polyline.setMap(map); marker.setDraggable(false); // Replace the buttons buttonsDiv.replaceChild(nextButton, submitButton); // Set map bounds to e