ene.add(helper); // var helperCamera = new THREE.CameraHelper(dLight.shadow.camera); // scene.add(helperCamera); // Initialize controls controls = new OrbitControls(camera, renderer.domElement); controls.enableDamping = true; controls.dynamicDampingFactor = 0.01; controls.enablePan = false; controls.minDistance = Math.min(globeWidth, globeHeight) / 2; controls.maxDistance = Math.min(globeWidth, globeHeight) / 2; controls.rotateSpeed = 0.8; controls.zoomSpeed = 1; controls.autoRotate = false; controls.minPolarAngle = Math.PI / 3.5; controls.maxPolarAngle = Math.PI - Math.PI / 3; } // Initialize the Globe function initGlobe() { // Initialize the Globe Globe = new ThreeGlobe({ waitForGlobeReady: true, animateIn: true, }) .hexPolygonsData(countries.features) .hexPolygonResolution(3) .hexPolygonMargin(0.7) .showAtmosphere(true) .atmosphereColor(\\"#ffffff\\") .atmosphereAltitude(0.1) .hexPolygonColor((e) => { if ( [\\"KEN\\", \\"CHN\\", \\"FRA\\", \\"ZAF\\", \\"JPN\\", \\"USA\\", \\"AUS\\", \\"CAN\\"].includes( e.properties.ISO_A3 ) ) { return \\"rgba(255,255,255, 1)\\"; } else return \\"rgba(255,255,255, 0.5)\\"; }); // NOTE Arc animations are followed after the globe enters the scene timeoutId = setTimeout(() => { Globe.arcsData(travelHistory.flights) .arcColor((e) => { return e.status ? \\"#9cff00\\" : \\"#ff2e97\\"; }) .arcAltitude((e) => { return e.arcAlt; }) .arcStroke((e) => { return e.status ? 0.5 : 0.3; }) .arcDashLength(0.9) .arcDashGap(4) .arcDashAnimateTime(1000) .arcsTransitionDuration(1000) .arcDashInitialGap((e) => e.order * 1) .labelsData(airportHistory.airports) .labelColor(() => \\"#ffffff\\") .labelDotOrientation((e) => { return e.text === \\"NGA\\" ? \\"top\\" : \\"right\\"; }) .labelDotRadius(0.35) .labelSize((e) => e.size) .labelText(\\"city\\") .labelResolution(6) .labelAltitude(0.01) .pointsData(airportHistory.airports) .pointColor(() => \\"#ffffff\\") .pointsMerge(true) .pointAltitude(0.07) .pointRadius(0.10); }, 1000); Globe.rotateX(-Math.PI * (