rControl.CurrentPID.KpDiv=10; MotorControl.SpeedPID.MaxOutput=10000;//+SpeedACTFilter.NOut>>12; MotorControl.SpeedPID.MinOutput=-10000; MotorControl.SpeedPID.MaxError=10000; MotorControl.SpeedPID.MinError=-10000; MotorControl.SpeedPID.KpDiv=10; MotorControl.VoltagePID.MaxOutput=0x7000; MotorControl.VoltagePID.MinOutput=-1*0x7000; MotorControl.VoltagePID.MaxError=1000; MotorControl.VoltagePID.MinError=-1000; MotorControl.VoltagePID.KpDiv=1; FilterInit(&MotorControl.CurrentDFilter); MotorControl.CurrentDFilter.NTime=1500; FilterInit(&MotorControl.ThorttleVoltage); MotorControl.ThorttleVoltage.NTime=5000; ThorttleVoltage.yk_1=0; ThorttleVoltage.coef=4700; CurrentDFilter.yk_1=0; CurrentDFilter.coef=4700*5; } void I_Park(void) { /*Park变换，电流由两相旋转坐标系到两相静止坐标系*/ /*输入变量：VoltageD,VoltageQ, AngleSin,AngleCos,输出变量VoltageX,VoltageY*/ /* VoltageX=VoltageD*AngleCos-VoltageQ*AngleSin; VoltageY=VoltageD*AngleSin+VoltageQ*AngleCos; */ int Temp1,Temp2; Temp1=MotorControl.VoltageD; Temp2=MotorControl.VoltageQ; MotorControl.VoltageX=((Temp1*MotorControl.AngleCos)-(Temp2*MotorControl.AngleSin))>>14; MotorControl.VoltageY=((Temp2*MotorControl.AngleCos)+(Temp1*MotorControl.AngleSin))>>14; } void MyPark(void) { /*Park变换，电流由两相静止坐标系到两相旋转坐标系*/ /*输入变量：CurrentX,CurrentY, AngleSin,AngleCos,输出变量CurrentD,CurrentQ*/ /* CurrentD=CurrentX*AngleCos+CurrentY*AngleSin; CurrentQ=-CurrentX*AngleSin+CurrentY*AngleCos; */ MotorControl.CurrentD=(long int)((long int)(MotorControl.CurrentX*MotorControl.AngleCos)+(long int)(MotorControl.CurrentY*MotorControl.AngleSin))>>14; MotorControl.CurrentQ=(long int)((long int)(MotorControl.CurrentY*MotorControl.AngleCos)-(long int)(MotorControl.CurrentX*MotorControl.AngleSin))>>14; } void SinCosGet2(void) { int Temp; Temp=MotorControl.StopAngle+128; while(Temp>=256)Temp=Temp-256; while(Temp<0)Temp=Temp+256; MotorControl.AngleSin=SinTab[Temp]; Temp=Temp+64; while(Temp>=256)Temp=Temp-256; MotorControl.AngleCos=SinTab[Temp]; } /******************************************************************************* 函数名称： void lowPass_filter(stru_RC_Def *rc, s16 signal) 功能描述： 低通滤波器 操作的表： 无 输入参数： 无 输出参数： 无 返 回 值： 无 多任务访问: 该函数涉及全局表项操作，不可重入 其它说明： 修改日期 版本号 修改人 修改内容 ----------------------------------------------------------------------------- 2022/06/15 V1.0 LiuH 创建 *******************************************************************************/ s16 lowPass_filter(stru_RC_Def *rc, s16 signal) { s32 wkg; wkg = (signal - (s16)(rc->yk_1 >> 16)) * (rc->coef); rc->yk_1 += wkg; return(rc->yk_1 >> 16); } void SVPWMOutput(void) { /*PWM输出程序*/ /*输入变量VoltageU,VoltageV,VoltageW,输出CMPR1,CMPR2,CMPR3*/ /* CMPR1=PWMPRD-(VoltageU+0x4000)*PWMPRD>>16; CMPR2=PWMPRD-(VoltageV+0x4000)*PWMPRD>>16; CMPR3=PWMPRD-(VoltageW+0x4000)*PWMPRD>>16; */ int Temp1,Temp2,Temp3,Temp; int Sector; Sector=0; MotorControl.VoltageU=MotorControl.VoltageY; Temp=MotorControl.VoltageX; Temp=Temp*28377; Temp=Temp>>14; Temp=-1*Temp; Temp=Temp-MotorControl.VoltageY; MotorControl.VoltageW=Temp>>1; Temp=MotorControl.VoltageX; Temp=Temp*28377; Temp=Temp>>14; Temp=1*Temp; Temp=Temp-MotorControl.VoltageY; MotorControl.VoltageV=Temp>>1; if(MotorControl.VoltageU>0)Sector=1; if(MotorControl.VoltageV>0)Sector=Sector+2; if(MotorControl.VoltageW>0)Sector=Sector+4; MotorControl.VoltageU=MotorControl.VoltageY; Temp=MotorControl.VoltageX; Temp=Temp*28377; Temp=Temp>>14; Temp=-1*Temp; Temp=Temp+MotorControl.VoltageY; MotorControl.VoltageW=Temp>>1; Temp=MotorControl.VoltageX; Temp=Temp*28377; Temp=Temp>>14; Temp=Temp; Temp=Temp+MotorControl.VoltageY; MotorControl.VoltageV=Temp>>1; // VoltageV=-1*VoltageU-VoltageW; switch(Sector) { case 0: //特殊情况, VoltgaeX=0;VoltageY=0; // EvaRegs.CMPR1=PWMPRD; // EvaRegs.CMPR2=PWMPRD; // EvaRegs.CMPR3=PWMPRD; MotorControl.VoltageU=0; MotorControl.VoltageV=0; MotorControl.VoltageW=0; break; case 1: //T1=Z,T2=Y Temp1=MotorControl.VoltageW; Temp2=MotorControl.VoltageV; /* Temp3=Temp1+Temp2; if(Temp3>=(long int)(0x7FFF)) { Temp1=Temp1*0x7FFF; Temp1=Temp1/Temp3; Temp2=Temp2*0x7FFF; Temp2=Temp2/Temp3; } */ Temp3=Temp1+Temp2; // Temp3=Temp3>>1; MotorControl.VoltageV=0x7FFF-Temp3; MotorControl.VoltageV=MotorControl.VoltageV>>1; MotorControl.VoltageU=MotorControl.VoltageV+Temp1; MotorControl.VoltageW=MotorControl.VoltageU+Temp2; MotorControl.CurrentMeasure=1; break; case 2: //T1=Y,T2=-X Temp1=MotorControl.VoltageV; Temp2=-1*MotorControl.VoltageU; /* Temp3=Temp1+Temp2; if(Temp3>=(long int)(0x7FFF)) { Temp1=Temp1*0x7FFF; Temp1=Temp1/Temp3; Temp2=Temp2*0x7FFF; Temp2=Temp2/Temp3; } */ Temp3=Temp1+Temp2; // Temp3=Temp3>>1; MotorControl.VoltageU=0x7FFF-Temp3; MotorControl.VoltageU=MotorControl.VoltageU>>1; MotorControl.VoltageW=MotorControl.VoltageU+Temp1; MotorControl.VoltageV=MotorControl.VoltageW+Temp2; MotorControl.CurrentMeasure=2; break; case 3: //T1=-Z,T2=X Temp1=-1*MotorControl.VoltageW; Temp2=MotorControl.VoltageU; /* Temp3=Temp1+Temp2; if(Temp3>=(long int)(0x7FFF)) { Temp1=Temp1*0x7FFF; Temp1=Temp1/Temp3; Temp2=Temp2*0x7FFF; Temp2=Temp2/Temp3; } */ Temp3=Temp1+Temp2; // Temp3=Temp3>>1; MotorControl.VoltageU=0x7FFF-Temp3; MotorControl.VoltageU=MotorControl.VoltageU>>1; MotorControl.VoltageV=MotorControl.VoltageU+Temp1; MotorControl.VoltageW=MotorControl.VoltageV+Temp2; MotorControl.CurrentMeasure=2; break; case 4: //T1=-X,T2=Z Temp1=-1*MotorControl.VoltageU; Temp2=MotorControl.VoltageW; /* Temp3=Temp1+Temp2; if(Temp3>=(long int)(0x7FFF)) { Temp1=Temp1*0x7FFF; Temp1=Temp1/Temp3; Temp2=Temp2*0x7FFF; Temp2=Temp2/Temp3; } */ Temp3=Temp1+Temp2; // Temp3=Temp3>>1; MotorControl.VoltageW=0x7FFF-Temp3; MotorControl.VoltageW=MotorControl.VoltageW>>1; MotorControl.VoltageV=MotorControl.VoltageW+Temp1; MotorControl.VoltageU=MotorControl.VoltageV+Temp2; MotorControl.CurrentMeasure=3; break; case 5: //T1=X,T2=-Y Temp1=MotorControl.VoltageU; Temp2=-1*MotorControl.VoltageV; /* Temp3=Temp1+Temp2; if(Temp3>=(long int)(0x7FFF)) { Temp1=Temp1*0x7FFF; Temp1=Temp1/Temp3; Temp2=Temp2*0x7FFF; Temp2=Temp2/Temp3; } */ Temp3=Temp1+Temp2; // Temp3=Temp3>>1; MotorControl.Volta