 /// @param kernel_ Kernel address /// @param decimals_ Decimals that all prices will be returned with /// @param observationFrequency_ Frequency at which prices are stored for moving average constructor(Kernel kernel_, uint8 decimals_, uint32 observationFrequency_) Module(kernel_) { decimals = decimals_; observationFrequency = observationFrequency_; } // ========== KERNEL FUNCTIONS ========== // /// @inheritdoc Module function KEYCODE() public pure override returns (Keycode) { return toKeycode(\\"PRICE\\"); } /// @inheritdoc Module function VERSION() external pure override returns (uint8 major, uint8 minor) { major = 2; minor = 0; } // ========== MODIFIERS ========== // //////////////////////////////////////////////////////////////// // DATA FUNCTIONS // //////////////////////////////////////////////////////////////// // ========== ASSET INFORMATION ========== // /// @inheritdoc PRICEv2 function getAssets() external view override returns (address[] memory) { return assets; } /// @inheritdoc PRICEv2 function getAssetData(address asset_) external view override returns (Asset memory) { return _assetData[asset_]; } // ========== ASSET PRICES ========== // /// @inheritdoc PRICEv2 /// @dev Optimistically uses the cached price if it has been updated this block, otherwise calculates price dynamically /// /// @dev Will revert if: /// @dev - \`asset_\` is not approved /// @dev - No price could be determined function getPrice(address asset_) external view override returns (uint256) { // Try to use the last price, must be updated on the current timestamp // getPrice checks if asset is approved (uint256 price, uint48 timestamp) = getPrice(asset_, Variant.LAST); if (timestamp == uint48(block.timestamp)) return price; // If last price is stale, use the current price (price, ) = _getCurrentPrice(asset_); return price; } /// @inheritdoc PRICEv2 /// @dev Will revert if: /// @dev - \`asset_\` is not approved /// @dev - No price could be determined function getPrice(address asset_, uint48 maxAge_) external view override returns (uint256) { // Try to use the last price, must be updated more recently than maxAge // getPrice checks if asset is approved (uint256 price, uint48 timestamp) = getPrice(asset_, Variant.LAST); if (timestamp >= uint48(block.timestamp) - maxAge_) return price; // If last price is stale, use the current price (price, ) = _getCurrentPrice(asset_); return price; } /// @inheritdoc PRICEv2 /// @dev Will revert if: /// @dev - \`asset_\` is not approved /// @dev - No price could be determined /// @dev - An invalid variant is requested function getPrice( address asset_, Variant variant_ ) public view override returns (uint256 _price, uint48 _timestamp) { // Check if asset is approved if (!_assetData[asset_].approved) revert PRICE_AssetNotApproved(asset_); // Route to correct price function based on requested variant if (variant_ == Variant.CURRENT) { return _getCurrentPrice(asset_); } else if (variant_ == Variant.LAST) { return _getLastPrice(asset_); } else if (variant_ == Variant.MOVINGAVERAGE) { return _getMovingAveragePrice(asset_); } } /// @notice Gets the current price of the asset /// @dev This function follows this logic: /// @dev - Get the price from each feed /// @dev - If using the moving average, append the moving average to the results /// @dev - If there is only one price and it is not zero, return it /// @dev - Process the prices with the configured strategy /// /// @dev Will revert if: /// @dev - The resulting price is zero /// @dev - The configured strategy cannot aggregate the prices /// /// @param asset_ Asset to get the price of /// @return The price of the asset and the current block timestamp function _getCurrentPrice(address asset_) internal view returns (uint256, uint48) { Asset storage asset = _assetData[asset_]; // Iterate through feeds to get prices to aggregate with strategy Component[] memory feeds = abi.decode(asset.feeds, (Component[])); uint256 numFeeds = feeds.length; uint256[] memory prices = asset.useMovingAverage ? new uint256[](numFeeds + 1) : new uint256[](numFeeds); uint8 _decimals = decimals; // cache in memory to save gas for (uint256 i; i < numFeeds; ) { (bool success_, bytes memory data_) = address(_getSubmoduleIfInstalled(feeds[i].target)) .staticcall( abi.encodeWithSelector(feeds[i].selector, asset_, _decimals, feeds[i].params) ); // Store price if successful, otherwise leave as zero // Idea is that if you have several price calls and just // one fails, it'll DOS the contract with this revert. // We handle faulty feeds in the strategy contract. if (success_) prices[i] = abi.decode(data_, (uint256)); unchecked { ++i; } } // If moving average is used in strategy, add to end of prices array if (asset.useMovingAverage) prices[numFeeds] = asset.cumulativeObs / asset.numObservations; // If there is only one price, ensure it is not zero and return // Otherwise, send to strategy to aggregate if (prices.length == 1) { if (prices[0] == 0) revert PRICE_PriceZero(asset_); return (prices[0], uint48(block.timestamp)); } else { // Get price from strategy Component memory strategy = abi.decode(asset.strategy, (Component)); (bool success, bytes memory data) = address(_getSubmoduleIfInstalled(strategy.target)) .staticcall(abi.encodeWithSelector(strategy.selector, prices, strategy.params)); // Ensure call was successful if (!success) revert PRICE_StrategyFailed(asset_, data); // Decode asset price uint256 price = abi.decode(data, (uint256)); // Ensure value is not zero if (price == 0) revert PRICE_PriceZero(asset_); return (price, uint48(block.timestamp)); } } /// @notice Gets the last cached price of the asset /// @dev This function follows this logic: /// @dev - Get the last observation stored for the asset and return it /// /// @dev If no price has been ever cached, this function will automatically /// @dev return (0, 0). /// /// @param asset_ Asset to get the price of /// @return The price of the asset and asset's last observation time function _getLastPrice(address asset_) internal view returns (uint256, uint48) { // Load asset data Asset memory asset = _assetData[asset_]; // Get last observation stored for asset uint256 lastPrice = asset.obs[ asset.nextObsIndex == 0 ? asset.numObservations - 1 : asset.nextObsIndex - 1 ]; // Last price doesn't have to be checked for zero because it is checked before being stored // Return last price and time return (lastPrice, asset.lastObservationTime); } /// @notice Gets the moving average price of the asset /// @dev This function follows this logic: /// @dev - Calculate the moving average using the \`cumulativeObs\` / \`numObservations\` /// /// @dev Will revert if: /// @dev - The moving average is not stored for the asset /// /// @param asset_ Asset to get the price of /// @return The price of the asset and asset's last observation time function _getMovingAveragePrice(address asset_) internal view returns (uint256, uint48) { // Load asset data Asset memory asset = _assetData[asset_]; // Check if moving average is stored for asset if (!asset.storeMovingAverage) revert PRICE_MovingAverageNotStored(asset_); // Calculate moving average uint256 movingAverage = asset.cumulativeObs / asset.numObservations; // Moving average doesn't have to be checked for zero because each value is checked before being stored // Return moving average and time return (movingAverage, asset.lastObservationTime); } /// @inheritdoc PRICEv2 /// @dev Optimistically uses the cached price if it has been updated this block, otherwise calculates price dynamically function getPriceIn(address asset_, address base_) external view override returns (uint256) { // Get the last price of each asset (getPrice checks if asset is approved) (uint256 assetPrice, uint48 assetTime) = getPrice(asset_, Variant.LAST); (uint256 basePrice, uint48 baseTime) = getPrice(base_, Variant.LAST); // Try to use the last prices, timestamp must be current // If stale, get current price if (assetTime != uint48(block.timestamp)) { (assetPrice, ) = _getCur